using Renewal.Application.Models.Client;
using Renewal.Application.Validator;
using Xunit;

namespace Renewal.Application.UnitTests.Validator
{
    public class CreateClientRequestValidatorTest
    {
        private readonly CreateClientRequest _model;
        private readonly CreateClientRequestValidator _validator;

        public CreateClientRequestValidatorTest()
        {
            _model = new CreateClientRequest
            {
                Name = "test"
            };

            _validator = new CreateClientRequestValidator();
        }

        [Fact]
        public void ShouldSuccess_When_Model_Is_Valid()
        {
            var result = _validator.Validate(_model);
            Assert.True(result.IsValid);
        }

        [Theory]
        [InlineData(null)]
        [InlineData("")]
        public void ShouldFail_When_Name_Is_Empty(string name)
        {
            _model.Name = name;
            var result = _validator.Validate(_model);
            Assert.False(result.IsValid);
        }

        [Theory]
        [InlineData("")]
        public void ShouldFail_When_Name_Is_Greater_Than_250Chars(string name)
        {
            _model.Name = name;
            var result = _validator.Validate(_model);
            Assert.False(result.IsValid);

        }
    }
}
